4.query:
fetch bizevents, scanLimitGBytes:-1
| filter event.provider == "azure-devops" and event.type == "pipeline"
| filter `project-name` == $AzureDevOps_ProjectName
| fieldsRename `pipeline-username`, alias: `Username`
| summarize countDistinct("pipeline-number"),by:{Username, `pipeline-status`}
| summarize Builds = count(), by:{`Username`, `pipeline-status`}
